import React from "react";
import { useState } from 'react';

const Results: React.FC<ResultsProps> = ({data}) => {

    const [info, setInfo] = useState(null);

    const fetchInput = () => {
          //fetch(`https://api.tvmaze.com/search/shows?q=${input}`)
          fetch("https://api.tvmaze.com/search/shows?q=breaking")
          .then((response) => response.json())
          .then((json) => {
             setInfo(json)
          
        });
      };

    var showInfo = info.data.map((element:any) => {
        return (
            <li key={element.show.id} className ="show">
                <div className = "showBox">
                    <a href={element.show.url} className="showLink">{element.show.name}</a>
                </div>
                <div>
                    {element.show.image (<img src = {element.show.image.medium} alt={element.show.name} />)}
                </div>
            </li>
        )
    });

    return (
        <ul className = "showBox">
            {showInfo}
            </ul>        
    );
};

export default Results;
